
public class Application {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Double slash to add comments /* to open, */ to close
		 /* primitive:
		  * int - integer
		  * double - Decimal places i.e 32.89235
		  * float - precision decimal data type i.e 45.3987
		  * long - like an int, but numbers can be higher
		  * short - like an int, but numbers can be less
		  * byte - 8 bits of data
		  * char - a single character - a B A &
		  * boolean - true or false
		  * 
		  * Objects:
		  * String - Textual data, a string of characters
		  */
		
		 //Variable Declaration - type, identifier, assignment operator, value, semi-colon
		 int samsAge = 67;
		 double accountBalance = 34.67;
		 char middleInitial = 'C';
		 boolean isHotOutside = true;
		 String firstName = "Sam";
		 
		 System.out.println(samsAge);
		 System.out.println(accountBalance);
		 System.out.println(middleInitial);
		 System.out.println(isHotOutside);
		 System.out.println(firstName);
	}

}
